package com.bootexample4.RoostTest;

import io.restassured.RestAssured;
import io.restassured.http.ContentType;
import io.restassured.response.Response;
import io.restassured.specification.RequestSpecification;
import org.junit.Before;
import org.junit.Test;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.nio.file.Paths;
import java.util.*;

import static org.hamcrest.CoreMatchers.is;
import static org.junit.Assert.assertThat;

public class scenario3Test {

    private String baseUrl;

    @Before
    public void setUp() {
        this.baseUrl = System.getenv("API_HOST");
    }

    @Test
    public void createPetWithBodyTest() {

        String csvFilePath = Paths.get("scenario3Test.csv").toString();
        List<Map<String, String>> dataEntries = readDataEntries(csvFilePath);
        for(Map<String, String> dataEntry : dataEntries) {

            String method = dataEntry.get("METHOD");
            String url = dataEntry.get("URL");
            String headers = dataEntry.get("REQ_HEADERS");
            String reqBody = dataEntry.get("REQ_BODY");
            int expectedRespCode = Integer.parseInt(dataEntry.get("RESPONSE_CODE"));
            String expectedRespBody = dataEntry.get("RESPONSE_BODY");

            RequestSpecification request = RestAssured.with()
                    .baseUri(baseUrl)
                    .basePath(url);

            if (headers != null && !headers.isEmpty()) {
                Map<String, String> headersMap = new Gson().fromJson(headers, new TypeToken<Map<String, String>>() {}.getType());
                request.headers(headersMap);
            }

            if (reqBody != null && !reqBody.isEmpty()) {
                request.body(reqBody)
                        .contentType(ContentType.JSON);
            }

            Response response;

            if ("POST".equalsIgnoreCase(method)) {
                response = request.post(); // send POST request
            } else {
                throw new RuntimeException("Unknown or unsupported method: " + method);
            }

            assertThat(response.getStatusCode(), is(expectedRespCode));

            String actualRespBody = response.getBody().asString().trim();
            assertThat(actualRespBody, is(expectedRespBody));
        }
    }

    private List<Map<String, String>> readDataEntries(String filePath) {
        List<Map<String, String>> entries = new ArrayList<>();
        String[] headers = {"METHOD","URL","REQ_HEADERS","REQ_BODY","RESPONSE_CODE","RESPONSE_BODY",};
        try (BufferedReader br = new BufferedReader(new FileReader(filePath))) {
            String line = br.readLine(); // Skip header
            while ((line = br.readLine()) != null) {
                String[] values = line.split("\\^\\|\\^");
                Map<String, String> dataEntry = new LinkedHashMap<>();
                for (int i = 0; i < values.length; i++) {
                    dataEntry.put(headers[i], values[i]);
                }
                entries.add(dataEntry);
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
        return entries;
    }

}
